import React from "react";
import BookingField from "../component/BookingField";
import DisplayImage from "../component/DisplayImage";
import Properties from "../component/Properties";
import { useParams } from "react-router-dom";

function Display() {
	const { id } = useParams();
	console.warn(`cookie===${document.cookie}`);
	// Function to get the value of a cookie by its name
	function getCookie(cookieName) {
		const cookies = document.cookie.split(";");
		for (let i = 0; i < cookies.length; i++) {
			const cookie = cookies[i].trim();
			if (cookie.startsWith(`${cookieName}=`)) {
				return cookie.substring(cookieName.length + 1);
			}
		}
		return null; // Return null if the cookie is not found
	}

	// Example usage to get the value of "jwtToken" cookie
	const jwtToken = getCookie("jwtToken");

	// Now you can use jwtToken as needed
	console.log(`JWT Token: ${jwtToken}`);

	return (
		<div className="container-fluid m-0 p-0">
			<DisplayImage />
			<br />
			<BookingField id={id} />
			<Properties />
		</div>
	);
}

export default Display;
