import Axios from "axios";
import { useNavigate } from "react-router-dom";
import { refresh } from "./Refresh";

const intercept = Axios.create({
	withCredentials: true,
	headers: { "Content-Type": "application/json" },
});

// Move the useNavigate hook call inside a functional component or a custom hook
const getRequestInterceptor = () => {
	const navigate = useNavigate();

	return intercept.interceptors.request.use(function (config) {
		const authToken = localStorage.getItem("jwtToken");
		intercept.headers.Authorization = `Bearer ${authToken}`;

		if (!authToken) {
			navigate("/login");
		}

		return config;
	});
};

const responseIntercept = intercept.interceptors.response.use(
	(response) => response,
	async (error) => {
		const prevRequest = error?.config;

		if (error?.response?.status === 403 && !prevRequest?.sent) {
			try {
				await refresh();
				return Axios(prevRequest);
			} catch (refreshError) {
				console.error("Refresh failed", refreshError);
				useNavigate()("/login");
			}
		}

		return Promise.reject(error);
	}
);

// Use the getRequestInterceptor function to get the interceptor
getRequestInterceptor();

