import Axios from "axios";
import { useNavigate } from "react-router-dom";
import { refresh } from "./Refresh";

const intercept = Axios.create({
	withCredentials: true,
	headers: { "Content-Type": "application/json" },
});

const responseIntercept = intercept.interceptors.response.use(
	(response) => response,
	async (error) => {
		const prevRequest = error?.config; // Correct the usage of prevRequest
		if (error?.response?.status === 403 && !prevRequest?.sent) {
			try {
				// Call the refresh function and await its completion
				await refresh();
				// Retry the original request after successful refresh
				return Axios(prevRequest);
			} catch (refreshError) {
				// Handle refresh errors here, for example, redirect to login
				console.error("Refresh failed", refreshError);
				// Redirect to the login page
				useNavigate()("/login");
			}
		}
		return Promise.reject(error); // Re-throw the error if not handled
	}
);

intercept.interceptors.request.use(function (config) {
	const authToken = localStorage.getItem("jwtToken");
	console.log(`Bearer ${authToken}`);
	intercept.headers.Authorization = `Bearer ${authToken}`;
	if (!authToken) {
		useNavigate()("/login");
	}
	return config; // Return the modified config for the request
});

// Correct theusage of intercept.post and intercept.get
export const postRequest = async (api, data) => {
	try {
		const res = await intercept.post(api, data);
		return res.data;
	} catch (error) {
		console.error("Post request failed", error);
		throw error;
	}
};

export const getRequest = async (api) => {
	try {
		const res = await intercept.get(api);
		return res.data;
	} catch (error) {
		console.error("Get request failed", error);
		throw error;
	}
};
